var PolygonGrid=function(){this.polygons=[],this.bounds=[],this.add_polygon=function(o){this.polygons.push(o)},this.closest_edge=function(o,n){for(var s=null,t=Number.MAX_SAFE_INTEGER,i=0;i<this.polygons.length;++i){var l=this.polygons[i].distance_to_edge_boundary(o,n);l&&t>l&&(s=this.polygons[i].closest_edge(o,n),t=l)}},this.polygon_under=function(o,n){for(var s=0;s<this.polygons.length;++s)if(this.polygons[s].is_under(o,n))return this.polygons[s];return null},this.set_bounds=function(o){this.bounds=o},this.draw=function(){for(var o=0;o<this.polygons.length;++o)this.polygons[o].draw()}};