var PolygonGrid=function(){this.polygons=[],this.bounds=[],this.add_polygon=function(o){this.polygons.push(o)},this.new_polygon_on_edge=function(o,n){var s=new NGon(n);s.initialize_from_line(o),this.polygons.push(s)},this.closest_edge=function(o,n){for(var s=null,t=Number.MAX_SAFE_INTEGER,i=0;i<this.polygons.length;++i){var r=this.polygons[i].distance_to_edge_boundary(o,n);r&&t>r&&(s=this.polygons[i].closest_edge(o,n),t=r)}},this.polygon_under=function(o,n){for(var s=0;s<this.polygons.length;++s)if(this.polygons[s].is_under(o,n))return this.polygons[s];return null},this.set_bounds=function(o){this.bounds=o},this.draw=function(){for(var o=0;o<this.polygons.length;++o)this.polygons[o].draw()},this.to_JSON=function(){for(var o=[],n=0;n<this.polygons.length;++n)o.push(this.polygons[n].to_vertex_position_array);var s={boundary:this.bounds,polygons:o};return JSON.stringify(s)}};